generated.css:
* {
  font-family: "Lora", serif;
  font-size: 32px;
}

body {
  margin: 0;
}

::-webkit-scrollbar {
  display: none;
}

.completed-container {
  overflow-x: auto;
  white-space: nowrap;
  position: fixed;
  top: 0;
  line-height: 100vh;
  height: 100vh;
  width: 50%;
  left: 0;
  text-align: right;
  background: white;
  color: black;
}

.completed-container:after {
  content: " ";
  background-image: linear-gradient(to right, white, white, white, transparent);
  z-index: 5;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
}

.todo-container {
  background: black;
  color: white;
  position: fixed;
  top: 0;
  line-height: 100vh;
  height: 100vh;
  width: 50%;
  right: 0;
}

.todo-container:after {
  content: " ";
  background-image: linear-gradient(to right, transparent, black, black, black);
  z-index: 5;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

input {
  position: fixed;
  height: 100vh;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 10;
  background: transparent;
  color: transparent;
  border: none;
  -moz-appearance: none;
  -webkit-appearance: none;
  outline: none !important;
}
